import React, { Component } from "react";
// This will require to npm install axios
import axios from 'axios';
import './navbar.css';


export default class AddPackages extends Component {
  // This is the constructor that stores the data.
  constructor(props) {
    super(props);

    this.OnChangeKSize = this.OnChangeKSize.bind(this);
    this.state = {
    KSize: 0
    };
  }

  // These methods will update the state properties.
  onChangeKSize(e) {
    this.setState({
        KSize: e.target.value,
    });
  }
// This function will handle the submission.
  onSubmit(e) {
    e.preventDefault();


    axios
       .post("http://localhost:4000/app/grouporders", this.KSize)
      .then((res) => console.log(res.data))
      .catch((error) => console.log(error));
    // We will empty the state after posting the data to the database
    this.setState({
    KSize: 0
    });
  }

  // This following section will display the form that takes the input from the user.
  render() {
      <h1>Prints</h1>
    return (
      <div style={{ marginTop: 20 }}>
        <h3>Register</h3>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>Define your K-means: </label>
            <input
              type="text"
              className="form-control"
            value={this.state.KSize}
              onChange={this.OnChangeKSize}
            />
          </div>
          <div className="form-group">
            <input
              type="submit"
              value="Register"
              className="btn btn-primary"
            />
          </div>
        </form>
      </div>
    );
  }
}
