import React, { Component } from "react";
// This will require to npm install axios
import axios from 'axios';
import './navbar.css';


import PlacesAutocomplete, {
    geocodeByAddress,
    getLatLng,
  } from 'react-places-autocomplete';
   

export default class AddPackages extends Component {
  // This is the constructor that stores the data.
  constructor(props) {
    super(props);

    this.onChangePackageName = this.onChangePackageName.bind(this);
    this.onChangePackageLat = this.onChangePackageLat.bind(this);
    this.onChangePackageLong = this.onChangePackageLong.bind(this);
    this.onChangePackageAddress = this.onChangePackageAddress.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.state = {
    receiver_name: "",
    latitude: 0.0,
    longitude: 0.0,
    address: ""
    };
  }

  // These methods will update the state properties.
  onChangePackageName(e) {
    this.setState({
        receiver_name: e.target.value,
    });
  }

  onChangePackageLat(e) {
    this.setState({
        latitude: e.target.value,
    });
  }

  onChangePackageLong(e) {
    this.setState({
        longitude: e.target.value,
    });
  }

  onChangePackageAddress(e) {
    this.setState({
        address: e.target.value,
    });
  }

  // GeoConversion() {

