//       .then(results => getLatLng(results[0]))
//       .then(latLng => console.log('Success', latLng))
//       .catch(error => console.error('Error', error));


//   };


// This function will handle the submission.
  onSubmit(e) {
    e.preventDefault();

    ////////attempt to convert address to lat and long
