//     var address = this.state.address;
//    // var address_url = `https://maps.googleapis.com/maps/api/geocode/json?address=${address}&key=AIzaSyDBGo5pGarCnOBYo342QaXwZMEpuph4eOU`;


//     <script type="text/javascript" src={"http://maps.google.com/maps/api/js?sensor=false"}>

//         </script>

//     geocoder.geocode( {'address': address}, function(results, status) {

//     if (status == google.maps.GeocoderStatus.OK) {
//         var latitude = results[0].geometry.location.lat();
//         var longitude = results[0].geometry.location.lng();
//         alert(latitude);
//     }
//     }); 


    

    // When post request is sent to the create url, axios will add a new record(newperson) to the database.
    const newpackage = {
    //   person_name: this.state.person_name,
    //   person_position: this.state.person_position,
    //   person_level: this.state.person_level,
    "receiver_name": this.state.receiver_name,
    "latitude": this.state.latitude,
    "longitude": this.state.longitude,
    "address": this.state.adress
    };

    axios
       .post("http://localhost:4000/app/grouporders", newpackage)
      .then((res) => console.log(res.data))
      .catch((error) => console.log(error));
    // We will empty the state after posting the data to the database
    this.setState({
    //   person_name: "",
    //   person_position: "",
    //   person_level: "",
    receiver_name: "",
    latitude: 0.0,
    longitude: 0.0,
    address: ""
    });
  }

  // This following section will display the form that takes the input from the user.
  render() {
      <h1>Prints</h1>
    return (
      <div style={{ marginTop: 20 }}>
        <h3>Register</h3>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>Reicever Name: </label>
            <input
              type="text"
              className="form-control"
            value={this.state.receiver_name}
              onChange={this.onChangePackageName}
            />
          </div>
         <div className="form-group">
                    <label>Address: </label>
                    <input
                    type="text"
                    className="form-control"
                    value={this.state.address}
                    onChange={this.handleChange}
                    onSelect={this.handleSelect}
                    />
