              onChange={this.onChangePackageLat}
            />
          </div>
          <div className="form-group">
            <label>Longitude: </label>
            <input
              type="text"
              className="form-control"
              value={this.state.longitude}
              onChange={this.onChangePackageLong}
            />
          </div>
          <div className="form-group">
            <label>Address: </label>
            <input
              type="text"
              className="form-control"
